<?php
/**
 * Class: WSAL_Helpers_Assets
 *
 * @since   4.2.0
 * @package Wsal
 */

/**
 * Helper class unifying the loading process of some UI assets such as JS and/or CSS.
 *
 * @since 4.2.0
 */
class WSAL_Helpers_Assets {

	const DATEPICKER_DATE_FORMAT = 'yyyy-mm-dd';

	public static function loadDatepicker() {

		//  There is a time picker that still depends on this. It only displays along with datepickers so we load it here.
		$plugin_base_url = \WpSecurityAuditLog::GetInstance()->GetBaseUrl();
		wp_enqueue_script( 'wsal-datepick-plugin-js', $plugin_base_url . '/js/jquery.plugin.min.js', array( 'jquery' ), '5.1.0' );

		wp_enqueue_script( 'jquery-ui-datepicker' );
		//  https://jqueryui.com/themeroller/#!
		wp_enqueue_style( 'jquery-style', $plugin_base_url . '/css/jquery-ui/jquery-ui.min.css' );
		?>
        <script type="text/javascript">
            function wsal_CreateDatePicker($, $input, date) {
                $input.datepicker({
                    dateFormat: 'yy-mm-dd'
                });
                $input.attr('autocomplete', 'off')
            }

            function wsal_CheckDate(value) {
                // regular expression to match date format yyyy-mm-dd
                var re = /^(\d{4})-(\d{1,2})-(\d{1,2})$/;
                return value.match(re);
            }
        </script>
		<?php
	}

}